&lt;ul&gt;
&lt;li class=&quot;public-DraftStyleDefault-unorderedListItem public-DraftStyleDefault-reset public-DraftStyleDefault-depth0 public-DraftStyleDefault-listLTR&quot; data-block=&quot;true&quot; data-editor=&quot;431cp&quot; data-offset-key=&quot;2h8g6-0-0&quot;&gt;
